import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
    const [showAdmin, setShowAdmin] = useState(false);
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [content, setContent] = useState({});
    const [editContent, setEditContent] = useState({});
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    useEffect(() => {
        fetchContent();
    }, []);

    const fetchContent = async () => {
        const res = await axios.get('http://localhost:5000/api/content');
        setContent(res.data);
        setEditContent(res.data);
    };

    // Admin Login Page
    if (showAdmin && !isLoggedIn) {
        return (
            <div style={styles.loginContainer}>
                <div style={styles.loginBox}>
                    <h2>Admin Login</h2>
                    <input 
                        placeholder="Email" 
                        value={email} 
                        onChange={(e) => setEmail(e.target.value)}
                        style={styles.input}
                    />
                    <input 
                        type="password" 
                        placeholder="Password" 
                        value={password} 
                        onChange={(e) => setPassword(e.target.value)}
                        style={styles.input}
                    />
                    <button onClick={async () => {
                        const res = await axios.post('http://localhost:5000/api/auth/login', { email, password });
                        if (res.data.success) {
                            setIsLoggedIn(true);
                        } else {
                            alert('Invalid credentials');
                        }
                    }} style={styles.loginBtn}>
                        Login
                    </button>
                    <button onClick={() => setShowAdmin(false)} style={styles.backBtn}>
                        Back to Home
                    </button>
                </div>
            </div>
        );
    }

    // Admin Panel Page
    if (showAdmin && isLoggedIn) {
        return (
            <div style={styles.adminContainer}>
                <h2>Edit Content</h2>
                {Object.keys(editContent).map(key => (
                    <div key={key} style={styles.editSection}>
                        <label style={styles.label}>{key}</label>
                        <textarea
                            value={editContent[key] || ''}
                            onChange={(e) => setEditContent({...editContent, [key]: e.target.value})}
                            rows="3"
                            style={styles.textarea}
                        />
                    </div>
                ))}
                <button onClick={async () => {
                    await axios.put('http://localhost:5000/api/content', editContent);
                    setContent(editContent);
                    alert('Content Updated!');
                }} style={styles.saveBtn}>
                    Save All Changes
                </button>
                <button onClick={() => {
                    setIsLoggedIn(false);
                    setShowAdmin(false);
                }} style={styles.logoutBtn}>
                    Logout
                </button>
            </div>
        );
    }

    // Main Home Page (as per assignment screenshots)
    return (
        <div>
            {/* Navbar */}
            <nav style={styles.navbar}>
                <h2 style={styles.logo}>Megaplex Prime</h2>
                <button onClick={() => setShowAdmin(true)} style={styles.adminBtn}>
                    Admin
                </button>
            </nav>

            {/* Hero Section */}
            <div style={styles.hero}>
                <h1>{content.hero || 'Welcome to Megaplex Prime'}</h1>
            </div>

            {/* Project Overview */}
            <div style={styles.section}>
                <h2>Project Overview</h2>
                <p>{content.projectOverview || 'Loading...'}</p>
            </div>

            {/* Nearby Connectivity */}
            <div style={{...styles.section, backgroundColor: '#f5f5f5'}}>
                <h2>Nearby Connectivity</h2>
                <p style={{whiteSpace: 'pre-line'}}>{content.nearbyConnectivity || 'Loading...'}</p>
            </div>

            {/* Amenities */}
            <div style={styles.section}>
                <h2>Amenities</h2>
                <p>{content.amenities || 'Loading...'}</p>
            </div>

            {/* About Us */}
            <div style={{...styles.section, backgroundColor: '#f5f5f5'}}>
                <h2>About Us</h2>
                <p>{content.aboutUs || 'Loading...'}</p>
            </div>

            {/* Construction Updates */}
            <div style={styles.section}>
                <h2>Construction Updates</h2>
                <p style={{whiteSpace: 'pre-line'}}>{content.constructionUpdates || 'Loading...'}</p>
            </div>

            {/* FAQ */}
            <div style={{...styles.section, backgroundColor: '#f5f5f5'}}>
                <h2>FAQ</h2>
                <p style={{whiteSpace: 'pre-line'}}>{content.faq || 'Loading...'}</p>
            </div>
        </div>
    );
}

const styles = {
    navbar: {
        backgroundColor: '#1a2a3a',
        color: 'white',
        padding: '1rem 2rem',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center'
    },
    logo: { margin: 0 },
    adminBtn: {
        backgroundColor: 'transparent',
        border: '1px solid white',
        color: 'white',
        padding: '8px 16px',
        cursor: 'pointer'
    },
    hero: {
        backgroundColor: '#2c3e50',
        color: 'white',
        textAlign: 'center',
        padding: '80px 20px'
    },
    section: {
        padding: '60px 20px',
        maxWidth: '1200px',
        margin: '0 auto'
    },
    loginContainer: {
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#1a2a3a'
    },
    loginBox: {
        backgroundColor: 'white',
        padding: '40px',
        borderRadius: '8px',
        width: '100%',
        maxWidth: '400px'
    },
    input: {
        width: '100%',
        padding: '10px',
        margin: '10px 0',
        border: '1px solid #ddd',
        borderRadius: '4px'
    },
    loginBtn: {
        width: '100%',
        padding: '12px',
        backgroundColor: '#1a2a3a',
        color: 'white',
        border: 'none',
        borderRadius: '4px',
        cursor: 'pointer',
        margin: '10px 0'
    },
    backBtn: {
        width: '100%',
        padding: '12px',
        backgroundColor: '#6c757d',
        color: 'white',
        border: 'none',
        borderRadius: '4px',
        cursor: 'pointer'
    },
    adminContainer: {
        maxWidth: '800px',
        margin: '40px auto',
        padding: '20px'
    },
    editSection: {
        marginBottom: '20px'
    },
    label: {
        display: 'block',
        marginBottom: '5px',
        fontWeight: 'bold',
        textTransform: 'capitalize'
    },
    textarea: {
        width: '100%',
        padding: '10px',
        border: '1px solid #ddd',
        borderRadius: '4px',
        fontFamily: 'inherit'
    },
    saveBtn: {
        padding: '12px 24px',
        backgroundColor: '#28a745',
        color: 'white',
        border: 'none',
        borderRadius: '4px',
        cursor: 'pointer',
        marginRight: '10px'
    },
    logoutBtn: {
        padding: '12px 24px',
        backgroundColor: '#dc3545',
        color: 'white',
        border: 'none',
        borderRadius: '4px',
        cursor: 'pointer'
    }
};

export default App;
