import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
    const [page, setPage] = useState('home'); // 'home', 'login', 'admin'
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [content, setContent] = useState({});
    const [editContent, setEditContent] = useState({});
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    useEffect(() => {
        fetchContent();
    }, []);

    const fetchContent = async () => {
        const res = await axios.get('http://localhost:5000/api/content');
        setContent(res.data);
        setEditContent(res.data);
    };

    // LOGIN PAGE
    if (page === 'login') {
        return (
            <div style={styles.loginContainer}>
                <div style={styles.loginBox}>
                    <h2 style={styles.loginTitle}>Admin Login</h2>
                    <p style={styles.loginSubtitle}>Megaplex Prime</p>

                    <input 
                        type="email"
                        placeholder="Email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        style={styles.input}
                    />

                    <input 
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        style={styles.input}
                    />

                    <button 
                        onClick={async () => {
                            try {
                                const res = await axios.post('http://localhost:5000/api/auth/login', {
                                    email, password
                                });
                                if (res.data.success) {
                                    setIsLoggedIn(true);
                                    setPage('admin');
                                } else {
                                    alert('Invalid email or password! Use: admin@gmail.com / 1234');
                                }
                            } catch (err) {
                                alert('Login failed');
                            }
                        }}
                        style={styles.loginBtn}
                    >
                        Login
                    </button>

                    <button 
                        onClick={() => setPage('home')}
                        style={styles.backBtn}
                    >
                        ‚Üê Back to Home
                    </button>

                    <div style={styles.loginInfo}>
                        <p><strong>Demo Credentials:</strong></p>
                        <p>Email: admin@gmail.com</p>
                        <p>Password: 1234</p>
                    </div>
                </div>
            </div>
        );
    }

    // ADMIN PANEL
    if (page === 'admin' && isLoggedIn) {
        return (
            <div style={styles.adminContainer}>
                <div style={styles.adminHeader}>
                    <h2>Admin Panel - Edit Content</h2>
                    <button 
                        onClick={() => {
                            setIsLoggedIn(false);
                            setPage('home');
                        }}
                        style={styles.logoutBtn}
                    >
                        Logout
                    </button>
                </div>

                {Object.keys(editContent).map(key => (
                    <div key={key} style={styles.editSection}>
                        <label style={styles.label}>
                            {key.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase())}
                        </label>
                        <textarea
                            value={editContent[key] || ''}
                            onChange={(e) => setEditContent({...editContent, [key]: e.target.value})}
                            rows="4"
                            style={styles.textarea}
                        />
                    </div>
                ))}

                <button 
                    onClick={async () => {
                        await axios.put('http://localhost:5000/api/content', editContent);
                        setContent(editContent);
                        alert('‚úÖ Content updated successfully!');
                        setPage('home');
                    }}
                    style={styles.saveBtn}
                >
                    üíæ Save All Changes
                </button>
            </div>
        );
    }

    // HOME PAGE
    return (
        <div>
            {/* Navbar */}
            <nav style={styles.navbar}>
                <h2 style={styles.logo}>Megaplex Prime</h2>
                <button 
                    onClick={() => setPage('login')}
                    style={styles.adminBtn}
                >
                    Admin Login
                </button>
            </nav>

            {/* Hero Section */}
            <div style={styles.hero}>
                <h1 style={styles.heroTitle}>{content.hero || 'Welcome to Megaplex Prime'}</h1>
                <p style={styles.heroSubtitle}>Where Luxury Meets Comfort</p>
            </div>

            {/* Project Overview */}
            <div style={styles.section}>
                <h2 style={styles.sectionTitle}>Project Overview</h2>
                <p style={styles.sectionText}>{content.projectOverview || 'Loading...'}</p>
            </div>

            {/* Nearby Connectivity */}
            <div style={{...styles.section, backgroundColor: '#f8f9fa'}}>
                <h2 style={styles.sectionTitle}>Nearby Connectivity</h2>
                <p style={{...styles.sectionText, whiteSpace: 'pre-line'}}>
                    {content.nearbyConnectivity || 'Loading...'}
                </p>
            </div>

            {/* Amenities */}
            <div style={styles.section}>
                <h2 style={styles.sectionTitle}>Amenities</h2>
                <p style={styles.sectionText}>{content.amenities || 'Loading...'}</p>
            </div>

            {/* About Us */}
            <div style={{...styles.section, backgroundColor: '#f8f9fa'}}>
                <h2 style={styles.sectionTitle}>About Us</h2>
                <p style={styles.sectionText}>{content.aboutUs || 'Loading...'}</p>
            </div>

            {/* Construction Updates */}
            <div style={styles.section}>
                <h2 style={styles.sectionTitle}>Construction Updates</h2>
                <p style={{...styles.sectionText, whiteSpace: 'pre-line'}}>
                    {content.constructionUpdates || 'Loading...'}
                </p>
            </div>

            {/* FAQ */}
            <div style={{...styles.section, backgroundColor: '#f8f9fa'}}>
                <h2 style={styles.sectionTitle}>Frequently Asked Questions</h2>
                <p style={{...styles.sectionText, whiteSpace: 'pre-line'}}>
                    {content.faq || 'Loading...'}
                </p>
            </div>

            {/* Footer */}
            <footer style={styles.footer}>
                <p>¬© 2024 Megaplex Prime. All rights reserved.</p>
            </footer>
        </div>
    );
}

const styles = {
    // Navbar
    navbar: {
        backgroundColor: '#1a2a3a',
        color: 'white',
        padding: '1rem 2rem',
        display: 'flex',
